# Sync locations for all active configurations
configs = env['tracking.config'].search([('active', '=', True)])
for config in configs:
    try:
        api = env['tracking.api'].create({})
        api.sync_all_locations(config)
    except Exception as e:
        import logging
        _logger = logging.getLogger(__name__)
        _logger.error(f"Failed to sync locations for config {config.name}: {str(e)}")

# Sync device list for all active configurations
configs = env['tracking.config'].search([('active', '=', True)])
for config in configs:
    try:
        api = env['tracking.api'].create({})
        api.sync_all_devices(config)
    except Exception as e:
        import logging
        _logger = logging.getLogger(__name__)
        _logger.error(f"Failed to sync devices for config {config.name}: {str(e)}")
