body.script:
//
var devs = <t t-raw="devices"/>;
var tzOff = <t t-esc="timezone_offset or 3"/>;
var refInt = <t t-esc="refresh_interval"/>;
var map, mrks = {};

function col(s) {
    return s === 'online' ? '#28a745' : s === 'moving' ? '#007bff' : s === 'idle' ? '#ffc107' : '#dc3545';
}

function bdg(s) {
    return s === 'online' ? 'üü¢ Online' : s === 'moving' ? 'üîµ Moving' : s === 'idle' ? 'üü° Idle' : 'üî¥ Offline';
}

function tm(t) {
    if (!t) return 'N/A';
    try {
        var d = new Date(t + 'Z');
        d.setHours(d.getHours() + tzOff);
        return d.toLocaleString();
    } catch(e) { return t; }
}

function ini() {
    if (typeof L === 'undefined') { setTimeout(ini, 500); return; }

    map = L.map('map').setView([24.7136, 46.6753], 6);
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);

    var b = L.latLngBounds();
    devs.forEach(function(d) {
        var m = add(d);
        if (m) b.extend(m.getLatLng());
    });

    if (devs.length > 0) map.fitBounds(b, {padding: [50, 50]});
    if (refInt > 0) setInterval(ref, refInt);
}

function add(d) {
    var la = parseFloat(d.latitude), ln = parseFloat(d.longitude);
    if (!la || !ln) return null;

    var c = col(d.status), nm = d.name || d.device_id || 'Unknown';
    var m = L.marker([la, ln], {
        icon: L.divIcon({
            className: '',
            html: '<div style="position:relative"><div style="background:' + c + ';width:24px;height:24px;border-radius:50%;border:3px solid white;box-shadow:0 2px 6px rgba(0,0,0,0.4)"></div><div class="device-label">' + nm + '</div></div>',
            iconSize: [120, 40],
            iconAnchor: [60, 32]
        })
    }).addTo(map);

    m.bindPopup(
        '<div style="min-width:220px"><h3 style="margin:0 0 12px 0;color:#dc3545">üìç ' + nm + '</h3>' +
        '<p style="margin:6px 0"><b>Device:</b> ' + (d.device_id || 'N/A') + '</p>' +
        '<p style="margin:6px 0"><b>Status:</b> ' + bdg(d.status) + '</p>' +
        '<p style="margin:6px 0"><b>Speed:</b> ' + (d.speed || 0) + ' km/h</p>' +
        '<p style="margin:6px 0"><b>Driver:</b> ' + (d.driver_name || 'N/A') + '</p>' +
        '<p style="margin:6px 0"><b>Location:</b> ' + (d.address || 'Unknown') + '</p>' +
        '<p style="margin:6px 0;font-size:11px;color:#666">Last: ' + tm(d.last_update) + '</p></div>'
    );

    mrks[d.id] = m;
    return m;
}

function ref() {
    fetch('/idara_tracking/devices/json', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({jsonrpc: '2.0', method: 'call', params: {}, id: Date.now()})
    })
    .then(function(r) { return r.json(); })
    .then(function(dt) {
        var ds = dt.result;
        if (!ds || !Array.isArray(ds)) return;

        document.getElementById('device-count').textContent = ds.length + ' Devices';
        ds.forEach(function(d) {
            var m = mrks[d.id];
            if (m) {
                m.setLatLng([parseFloat(d.latitude), parseFloat(d.longitude)]);
                var c = col(d.status), nm = d.name || d.device_id || 'Unknown';
                m.setIcon(L.divIcon({
                    className: '',
                    html: '<div style="position:relative"><div style="background:' + c + ';width:24px;height:24px;border-radius:50%;border:3px solid white;box-shadow:0 2px 6px rgba(0,0,0,0.4)"></div><div class="device-label">' + nm + '</div></div>',
                    iconSize: [120, 40],
                    iconAnchor: [60, 32]
                }));
            } else { add(d); }
        });
    });
}

setTimeout(ini, 100);
//
