div.script:
//
    const initialDevices = <t t-raw="devices"/>;
    const refreshInterval = <t t-esc="refresh_interval"/>;
    const timezoneOffset = <t t-esc="timezone_offset or 3"/>;

    let map;
    const deviceMarkers = {};

    function initMap() {
        console.log('Initializing map...');

        if (typeof L === 'undefined') {
            console.error('Leaflet not loaded!');
            setTimeout(initMap, 500);
            return;
        }

        map = L.map('map').setView([24.7136, 46.6753], 6);

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '¬© OpenStreetMap',
            maxZoom: 19
        }).addTo(map);

        console.log('Map initialized, adding', initialDevices.length, 'devices');

        const bounds = L.latLngBounds();
        let markersAdded = 0;

        initialDevices.forEach(device => {
            const marker = createMarker(device);
            if (marker) {
                deviceMarkers[device.id.toString()] = marker;
                bounds.extend(marker.getLatLng());
                markersAdded++;
            }
        });

        console.log('Added', markersAdded, 'markers');

        if (markersAdded > 0) {
            map.fitBounds(bounds, { padding: [50, 50] });
        }

        if (refreshInterval > 0) {
            console.log('Auto-refresh enabled:', refreshInterval / 1000, 'seconds');
            setInterval(refreshDevices, refreshInterval);
        }
    }

    function getMarkerColor(status) {
        const colors = {
            'online': '#28a745',
            'moving': '#007bff',
            'idle': '#ffc107',
            'offline': '#dc3545'
        };
        return colors[status] || '#dc3545';
    }

    function getStatusBadge(status) {
        const badges = {
            'online': 'üü¢ Online',
            'moving': 'üîµ Moving',
            'idle': 'üü° Idle',
            'offline': 'üî¥ Offline'
        };
        return badges[status] || 'üî¥ Offline';
    }

    function adjustTimezone(dateStr) {
        if (!dateStr) return 'N/A';
        try {
            const date = new Date(dateStr + 'Z');
            date.setHours(date.getHours() + timezoneOffset);
            return date.toLocaleString('en-US', { 
                year: 'numeric', 
                month: '2-digit', 
                day: '2-digit',
                hour: '2-digit', 
                minute: '2-digit'
            });
        } catch(e) {
            return dateStr;
        }
    }

    function createMarker(device) {
        const lat = parseFloat(device.latitude);
        const lng = parseFloat(device.longitude);

        if (!lat || !lng || isNaN(lat) || isNaN(lng)) {
            console.warn('Invalid coordinates for device:', device.name);
            return null;
        }

        const color = getMarkerColor(device.status);
        const deviceName = device.name || device.device_id || 'Unknown';

        const marker = L.marker([lat, lng], {
            icon: L.divIcon({
                className: 'custom-marker',
                html: '<div style="position: relative;"><div style="background: ' + color + '; width: 24px; height: 24px; border-radius: 50%; border: 3px solid white; box-shadow: 0 2px 6px rgba(0,0,0,0.4);"></div><div class="device-label">' + deviceName + '</div></div>',
                iconSize: [120, 40],
                iconAnchor: [60, 32]
            })
        }).addTo(map);

        marker.bindPopup('<div style="min-width: 220px; font-family: Arial;"><h3 style="margin: 0 0 12px 0; color: #dc3545; font-size: 16px;">üìç ' + deviceName + '</h3><p style="margin: 6px 0; font-size: 13px;"><strong>Device ID:</strong> ' + (device.device_id || 'N/A') + '</p><p style="margin: 6px 0; font-size: 13px;"><strong>Status:</strong> ' + getStatusBadge(device.status) + '</p><p style="margin: 6px 0; font-size: 13px;"><strong>Speed:</strong> ' + (device.speed || 0) + ' km/h</p><p style="margin: 6px 0; font-size: 13px;"><strong>Driver:</strong> ' + (device.driver_name || 'N/A') + '</p><p style="margin: 6px 0; font-size: 13px;"><strong>Vehicle:</strong> ' + (device.vehicle_id || 'N/A') + '</p><p style="margin: 6px 0; font-size: 13px;"><strong>Location:</strong> ' + (device.address || 'Unknown') + '</p><p style="margin: 6px 0; font-size: 11px; color: #666;">Last update: ' + adjustTimezone(device.last_update) + '</p></div>');

        return marker;
    }

    function updateMarker(device) {
        const lat = parseFloat(device.latitude);
        const lng = parseFloat(device.longitude);

        if (!lat || !lng || isNaN(lat) || isNaN(lng)) {
            return;
        }

        const deviceKey = device.id.toString();
        const existingMarker = deviceMarkers[deviceKey];

        if (existingMarker) {
            const newLatLng = L.latLng(lat, lng);
            existingMarker.setLatLng(newLatLng);

            const color = getMarkerColor(device.status);
            const deviceName = device.name || device.device_id || 'Unknown';

            existingMarker.setIcon(L.divIcon({
                className: 'custom-marker',
                html: '<div style="position: relative;"><div style="background: ' + color + '; width: 24px; height: 24px; border-radius: 50%; border: 3px solid white; box-shadow: 0 2px 6px rgba(0,0,0,0.4);"></div><div class="device-label">' + deviceName + '</div></div>',
                iconSize: [120, 40],
                iconAnchor: [60, 32]
            }));
        } else {
            const marker = createMarker(device);
            if (marker) {
                deviceMarkers[deviceKey] = marker;
            }
        }
    }

    function refreshDevices() {
        console.log('Fetching updated device positions...');

        fetch('/idara_tracking/devices/json', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                jsonrpc: '2.0',
                method: 'call',
                params: {},
                id: new Date().getTime()
            })
        })
        .then(response => response.json())
        .then(data => {
            const devices = data.result;

            if (!devices || !Array.isArray(devices)) {
                console.error('Invalid devices data:', devices);
                return;
            }

            console.log('Received', devices.length, 'device updates');
            document.getElementById('device-count').textContent = devices.length + ' Devices';

            devices.forEach(device => {
                updateMarker(device);
            });
        })
        .catch(error => {
            console.error('Error fetching devices:', error);
        });
    }

    setTimeout(initMap, 100);
//
